import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import "./components/pages/Prijava.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from "react-router-dom";
import Navbar from './components/Navbar';
import Axios from "axios"
import Alert from "reactstrap/lib/Alert";
import {  Route, withRouter } from "react-router-dom";

export default function Prijava() {
  const [userName, setUserName] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError]=useState(false);
  function validateForm() {
    return userName.length > 0 && password.length > 0;
  }

  handleSubmit = event =>{
    event.preventDefault();
    Axios.get("http://localhost:3001/login?username="+userName+"&password="+password).then(result=>{
    console.log(result);
    console.log(result.data);  
    if(result==401) setError(true);
    else history.push("/radnik-profil", {username:userName, password:password});
    
  });
    
  }

  return (
    <>
    <Navbar />
    <div className="prijava-container">
    
    <div className="Login">
    <h1>Prijava za osoblje</h1>
      <Form onSubmit={this.handleSubmit}>
        <Form.Group size="lg" controlId="email">
          <Form.Label>Username</Form.Label>
          <Form.Control
            autoFocus
            type="username"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
          />
        </Form.Group>
        <Form.Group size="lg" controlId="password">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </Form.Group>
        {error && <Alert color="danger" fade={false}>
            <p style={{color: "red", fontWeight: "bold"}}>Uneseni podaci nisu ispravni!</p>
          </Alert>}
        <Link to="/radnik-profil">
        <Button block size="lg" type="submit" variant="primary" disabled={!validateForm()}>
          Prijava
        </Button>
        </Link>
      </Form>
      <div className="reg-div">
    
      </div>
      

    </div>
    </div>
    </>
  );
}
