import React, { Component } from 'react'
import moment from 'moment'

import NavbarRadnik from '../NavbarRadnik'
import './RadnikPregledRez.css'
import Datumcic from '../DatumPregledRez';
import DatePicker from "react-datepicker";




const today = moment().format('YYYY-MM-DD')
class RadnikPregledRez extends Component {
  constructor(props) {
    super(props);

    const rezervacija = [];
    

    for (let i = 0; i < 10; i++) {
        rezervacija.push({
            in: "2021-03-04",
            out: "2021-03-10",
            soba: 1,
            ime: "Emina Zahirovic",
            brGost: "2"

        });
    }

    this.state = { 
      pocDate: today,
      krajDate: today,
      brSobe: 1,
      brGostiju: 1,
      rezervacija };

      this.handleChangeDatePoc = this.handleChangeDatePoc.bind(this);
      this.handleChangeDateKraj = this.handleChangeDateKraj.bind(this);
      this.handleChangeBrSobe = this.handleChangeBrSobe.bind(this);
      this.handleChangeBrGostiju = this.handleChangeBrGostiju.bind(this);
}

handleChangeDate(event) {
  this.setState({pocDate: event.target.value, brSobe: this.state.brSobe, brGostiju:this.state.brGostiju, rezervacija: this.state.rezervacija});
}
handleChangeBrSobe(event) {
  this.setState({pocDate: this.state.pocDate , brSobe: event.target.value, brGostiju:this.state.brGostiju, rezervacija: this.state.rezervacija});
}
handleChangeBrGostiju(event) {
  this.setState({pocDate: this.state.pocDate , brSobe: this.state.brSobe, brGostiju:event.target.value, rezervacija: this.state.rezervacija});
}
   

  render () {
    return (
      <>
      <NavbarRadnik/>
      <div className="maincontainer">
      
       
        <h1 className="naslov">Pregled rezervacija</h1>

        <div className="filter">
        <h4>Filteri</h4>
        <hr></hr>
        <div className="filt-dio-1">
        <DatePicker filterDate={d => {
          return new Date() <d;
              }}
          format="dd-MM-yyyy"
          selected={this.state.pocDate}
          selectsStart
          pocDate={this.state.pocDate}
          krajDate={this.state.krajDate} // add the endDate to your startDate DatePicker now that it is defined
          onChange={date => {this.setState(state => ({
            pocDate: date,
            krajDate:date
          }));
          
          }}
        />
        <DatePicker filterDate={d => {
          return new Date() < this.state.startDate;
              }}
              format="dd-MM-yyyy"
          selected={this.state.krajDate}
          selectsEnd
          pocDate={this.state.pocDate}
          krajDate={this.state.pocDate}
          minDate={this.state.pocDate}
          onChange={date => {this.setState(state => ({
            krJDate: date
          })); 
          
        }}
        />
        </div>
        <div class="rowe">
          <div class="column">
          <p>Pretra≈æi po imenu klijenta</p>
          <input type="text" name="name" style={{width: "130px"}} placeholder="Ime klijenta"/>
            
          </div>
          
          <div class="column">
          <p>Odaberite broj sobe</p>
            <input type="number" id="brSobe" name="brSobe"  min="1" max="13"  onInput={this.handleChangeBrSobe.bind(this)} style={{width: "130px"}} placeholder="Broj sobe [1,13]"></input>
          </div>

          <div class="column">
             <p>Odaberite broj gostiju</p>
            <input type="number" id="brSobe" name="brSobe"  min="1" max="6"  onInput={this.handleChangeBrGostiju.bind(this)} style={{width: "131px"}} placeholder="Broj gostiju [1,6] sobe [1,13]"></input>
          </div>

        </div>
            
        </div>
        
        <div className="container mb-5 mt-5 text-left">
        <h4>Lista</h4>
        <hr></hr>
        
        <table class="table table-hover"  >
          <thead>
            <tr>
              <th>Ime</th>
              <th>Dolazak</th>
              <th>Odlazak</th>
              <th>Soba</th>
              <th>Broj gostiju</th>
            </tr>
          </thead>
          <tbody>
          {this.state.rezervacija.map((result) => {
            return (
             
                 <tr>
                  <td>{result.ime}</td>
                  <td>{result.in}</td>
                  <td>{result.out}</td>
                  <td>{result.soba}</td>
                  <td>{result.brGost}</td>
                </tr>
             
            )
          })}
           
            
          </tbody>
        </table>
       
            
      </div>
     
      </div>
        
      </>
    );
  }
}

export default RadnikPregledRez
