import React, { useState, useEffect } from 'react';
import { Button } from './Button';
import { Link } from 'react-router-dom';
import './Navbar.css';
import logo from '../images/logo-navbar.png';
import { ButtonDropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';

function NavbarRadnik() {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const [dropdownOpenPonuda, setOpenPonuda] = useState(false);
  const togglePonuda = () => setOpenPonuda(!dropdownOpenPonuda);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize', showButton);

  return (
    <>
      <nav className='navbar'>
        <div className='navbar-container'>
          <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
          <img src={logo} alt="website logo" class="logo-navbar" width="60px" height="60px"/>
            Vila Nezirović
            
          </Link>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>
            
            <li className='nav-item'>
                <ButtonDropdown isOpen={dropdownOpen} toggle={toggle}>
                <DropdownToggle caret>
                    Rezervacija
                </DropdownToggle>
                <DropdownMenu>
                <Link to='/radnik-dodaj-spec-pon'>
                    <DropdownItem>Dodaj rezervaciju</DropdownItem>
                </Link>
                <DropdownItem>Ažuriraj</DropdownItem>
                </DropdownMenu>
                </ButtonDropdown>
            </li>
            <li className='nav-item'>
            <ButtonDropdown isOpenPonuda={dropdownOpenPonuda} togglePonuda={togglePonuda}>
            <DropdownToggle caret>
                Specijalne ponude
            </DropdownToggle>
            <DropdownMenu>
            <Link to='/radnik-dodaj-spec-pon'>
                <DropdownItem>Dodaj ponudu</DropdownItem>
            </Link>
            <DropdownItem>Pregled ponuda</DropdownItem>
            </DropdownMenu>
            </ButtonDropdown>
            
            </li>
            <li className='nav-item'>
              <Link
                to='/rezervacija/0'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                Moj profil
              </Link>
            </li>

            
          </ul>
          
          
          
        </div>
      </nav>
    </>
  );
}

export default NavbarRadnik;
