import React, { Component } from 'react'
import moment from 'moment'

import NavbarRadnik from '../NavbarRadnik'
import './RadnikPregledRez.css'

import DatePicker from "react-datepicker";




const today = moment().format('YYYY-MM-DD')
class RadnikPregledRez extends Component {
  constructor(props) {
    super(props);

    const specPon = [];
    

    for (let i = 0; i < 10; i++) {
        specPon.push({
            vaziOd: "2021-03-04",
            vaziDo: "2021-03-10",
            sobeNaPopustu: [1,2,3,4],
            popust: 20, //U postocima popust
            kodPopusta: 1111,

        });
    }

    this.state = { 
      startDate: new Date(),
      endDate: new Date(),
      sobaPretr: 1,
      specPon };

     
      this.handleChangeBrSobaPretr = this.handleChangeBrSobaPretr.bind(this);
}


handleChangeBrSobaPretr(event) {
  this.setState({startDate: this.state.startDate ,endDate: this.state.endDate, sobaPretr: event.target.value, specPon: this.state.specPon});
}

   

  render () {
    return (
      <>
      <NavbarRadnik/>
      <div className="maincontainer">
      
       
        <h1 className="naslov">Pregled specijalnih ponuda</h1>

        <div className="filter">
        <h4>Filteri</h4>
        <hr></hr>
        <div className="filt-dio-1">
        <div className="rowe">
          <p>Specijalne ponude u vremenskom opsegu:</p>
            <div className="column"> 
            <p>Od:</p>
            <DatePicker 
                selected={this.state.startDate}
                selectsStart
                startDate={this.state.startDate}
                endDate={this.state.endDate} 
                onChange={date => this.setState(state => ({
                  startDate: date
                }))}
              />
          </div>
        
          <div className="column">
            <p>Do:</p>
            <DatePicker 
                selected={this.state.endDate}
                selectsEnd
                startDate={this.state.startDate}
                endDate={this.state.startDate}
                minDate={this.state.startDate}
                onChange={date => this.setState(state => ({
                  endDate: date
                }))}
              />
          </div>
          </div>
        </div>
        <div class="rowe">
          <div class="column">
          <p>Pretraži po imenu klijenta</p>
          <input type="text" name="name" style={{width: "200px"}} placeholder="Ime klijenta"/>
            
          </div>
          
          <div class="column">
          <p>Odaberite broj sobe</p>
            <input type="number" id="brSobe" name="brSobe"  min="1" max="13"  onInput={this.handleChangeBrSobe.bind(this)} style={{width: "130px"}} placeholder="Broj sobe [1,13]"></input>
          </div>

          <div class="column">
             <p>Odaberite broj gostiju</p>
            <input type="number" id="brSobe" name="brSobe"  min="1" max="6"  onInput={this.handleChangeBrGostiju.bind(this)} style={{width: "131px"}} placeholder="Broj gostiju [1,6] sobe [1,13]"></input>
          </div>

        </div>
            
        </div>
        
        <div className="container mb-5 mt-5 text-left">
        <h4>Lista</h4>
        <hr></hr>
        
        <table class="table table-hover"  >
          <thead>
            <tr>
              <th>Ime</th>
              <th>Dolazak</th>
              <th>Odlazak</th>
              <th>Soba</th>
              <th>Broj gostiju</th>
            </tr>
          </thead>
          <tbody>
          {this.state.rezervacija.map((result) => {
            return (
             
                 <tr>
                  <td>{result.ime}</td>
                  <td>{result.in}</td>
                  <td>{result.out}</td>
                  <td>{result.soba}</td>
                  <td>{result.brGost}</td>
                </tr>
             
            )
          })}
           
            
          </tbody>
        </table>
       
            
      </div>
     
      </div>
        
      </>
    );
  }
}

export default RadnikPregledRez
