import React, { useState, Component } from 'react'

import NavbarRadnik from '../NavbarRadnik';
import './RadnikDodajRez.css'
import FlavorForm from '../../components/Forma'
import Scheduler from '../MjesecView'
import 'react-bootstrap'


class RadnikDodajRez extends Component{
    constructor(props) {
        super(props);
        this.state = {
          datumDolaska: new Date(),
          datumOdlaska: new Date(),
          brOdrwslih: 1,
          brDjece: 0,

        };
    
        
      }
    
      promijeniBrOdraslih=(broj) =>{this.setState(state => ({
        ...state,
        brOdraslih:parseInt(broj)
      }))}
      promijeniBrDjece=(broj) =>{this.setState(state => ({
        ...state,
        brDjece:parseInt(broj)
      }))}
      promijeniBrSoba=(broj) =>{this.setState(state => ({
        ...state,
        brSoba:parseInt(broj)
      }))}
    
      render() {
        return (
    <>
        <NavbarRadnik/>
        <div className="dodaj-rez-container">
          <Scheduler/>

          <div className="forme-dodaj-rez">
          <p>Broj odraslih:</p>
                <FlavorForm id="odrasli" pocetniOdrasli={this.state.brOdraslih}  promijeniBrOdraslih={this.promijeniBrOdraslih}/>


          </div>
        </div>
        
        
        
    </>
        );
      }
    }

export default RadnikDodajRez;
