import React, { useState, useEffect } from 'react';
import { Button } from './Button';
import { Link } from 'react-router-dom';
import './Navbar.css';
import logo from '../images/logo-navbar.png';
import { ButtonDropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';

function NavbarRadnik() {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const [dropdownOpen, setOpen] = useState(false);

  const toggle = () => setOpen(!dropdownOpen);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize', showButton);

  return (
    <>
      <nav className='navbar'>
        <div className='navbar-container'>
          <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
          <img src={logo} alt="website logo" class="logo-navbar" width="60px" height="60px"/>
            Vila Nezirović
            
          </Link>
          <div className='menu-icon' onClick={handleClick}>
            <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
          </div>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>
            <li className='nav-item'>
              <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                Home
              </Link>
            </li>
            <li className='nav-item'>
              <Link
                to='/rezervacija/0'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                Rezervacija
              </Link>
            </li>
            <li className='nav-item'>
              <Link
                to='/ponude'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                Specijalne ponude
              </Link>
            </li>

            <li className='nav-item'>
              <Link
                to='/prijava'
                className='nav-links-mobile'
                onClick={closeMobileMenu}
              >
                <button type="button" class="btn btn-outline-light">Prijava</button>
              </Link>
              <Link to='/prijava' className='nav-links-prijava'>
              <button type="button" class="btn btn-outline-light">Prijava</button>
              </Link>
            </li>
          </ul>
          <ButtonDropdown isOpen={dropdownOpen} toggle={toggle}>
            <DropdownToggle caret>
                Button Dropdown
            </DropdownToggle>
            <DropdownMenu>
            <DropdownItem header>Header</DropdownItem>
            <DropdownItem disabled>Action</DropdownItem>
            <DropdownItem>Another Action</DropdownItem>
            <DropdownItem divider />
            <DropdownItem>Another Action</DropdownItem>
            </DropdownMenu>
        </ButtonDropdown>
          
          
        </div>
      </nav>
    </>
  );
}

export default NavbarRadnik;
