import React from 'react'
import NavbarRadnik from '../NavbarRadnik'

import './RadnikEditProfil.css'
import jane from '../../images/jane-doe.jpg';
import john from '../../images/john-doe.png';


export default class RadnikMojProfil extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      photoMale: john,
      photoFemale: jane,
      spol: 0,/*0 musko, 1 zensko*/
      profileFields: {
        
        username: "Nika Dangubić",
        firstName: "Nika",
        lastName: "Dangubić",
        
        id: '1312',
        jobTitle: 'Recepcioner',
        startDate: '23/05/2015',
        address: 'Ul. "Zmaja od Bosne" 1',
        city: 'Sarajevo',
        country: 'Bosna i Hercegovina',
        mobile: '+38762666666',
        email: 'nesto@gmail.com',
        jmbg: '0101980111111',
        birthday: '01/01/1980',
        placeOfBirth: 'Sarajevo',
        countryOfBirth: 'Bosna i Hercegovina'
        
      }
    }
    this.provjeriUnos();
    this.promijeniIme = this.promijeniIme.bind(this);
    this.promijeniPrezime = this.promijeniPrezime.bind(this);
    this.promijeniUsername = this.promijeniUsername.bind(this);
    this.promijeniAdresu = this.promijeniAdresu.bind(this);

    
  }
  provjeriUnos=()=>{if(this.state.ime!="" && this.state.email!="" && this.state.prezime!="" && this.state.brojTel!="") { this.setState(state => ({
    ...state,
    error:false
  })); return true;} else { this.setState(state => ({
    ...state,
    error:true
  })); return false;}}

  promijeniIme(e) {
    this.setState(state => ({
      ...state,
      firstName: e.target.value,
    }))
  }
  promijeniPrezime(e) {
    this.setState(state => ({
      ...state,
      lastName: e.target.value,
    }))
  }
  promijeniUsername(e) {
    this.setState(state => ({
      ...state,
      username: e.target.value,
    }))
  }
  promijeniAdresu(e) {
    this.setState(state => ({
      ...state,
      address: e.target.value,
    }))
  }

  render () {
    return (
      <div className="profile-page-edit">
        <NavbarRadnik/>
        
        <p>Ime:</p>
        <input    placeholder='Ime'
        value={this.state.firstName} onChange={(e) => { this.promijeniIme(e); this.provjeriUnos()} }/> 

        <p>Prezime:</p>
        <input    placeholder='Prezime'
        value={this.state.lastName} onChange={(e) => { this.promijeniPrezime(e); this.provjeriUnos()} }/> 

        <p>Username:</p>
        <input    placeholder='Username'
        value={this.state.username} onChange={(e) => { this.promijeniUsername(e); this.provjeriUnos()} }/> 

        <p>ID:</p>
        <input    placeholder='ID može promijeniti samo admin' size={30}
         disabled={true}/> 

        <p>Zaposlenje:</p>
        <input    placeholder={this.state.profileFields.jobTitle}
         disabled={true}/> 

        <p>Datum zapošljavanja:</p>
        <input    placeholder={this.state.profileFields.startDate}
         disabled={true}/> 




        
      </div>
    )
  }
}
