import React, { Component } from 'react'
import moment from 'moment'

import NavbarRadnik from '../NavbarRadnik'
import './RadnikPregledRez.css'

import DatePicker from "react-datepicker";




const today = moment().format('YYYY-MM-DD')
class RadnikPregledSpecPon extends Component {
  constructor(props) {
    super(props);

    const specPon = [];
    

    for (let i = 0; i < 10; i++) {
        specPon.push({
            vaziOd: "2021-03-04",
            vaziDo: "2021-03-10",
            sobeNaPopustu: 1,
            nazivSpecPon: "Rani booking",
            popust: 20
            

        });
    }

    this.state = { 
      startDate: new Date(),
      endDate: new Date(),
      brSobe: 1, //Za pretraživanje broja sobe koja je spada u spec ponudu
      specPon };

     
      this.handleChangeBrSobe = this.handleChangeBrSobe.bind(this);
      
}


handleChangeBrSobe(event) {
  this.setState({pocDate: this.state.pocDate , brSobe: event.target.value, rezervacija: this.state.rezervacija});
}

   

  render () {
    return (
      <>
      <NavbarRadnik/>
      <div className="maincontainer">
      
       
        <h1 className="naslov">Pregled rezervacija</h1>

        <div className="filter">
        <h4>Filteri</h4>
        <hr></hr>
        <div className="filt-dio-1">
        <div className="rowe">
          <p>Rezervacije u vremenskom opsegu:</p>
            <div className="column"> 
            <p>Od:</p>
            <DatePicker 
                selected={this.state.startDate}
                selectsStart
                startDate={this.state.startDate}
                endDate={this.state.endDate} 
                onChange={date => this.setState(state => ({
                  startDate: date
                }))}
              />
          </div>
        
          <div className="column">
            <p>Do:</p>
            <DatePicker 
                selected={this.state.endDate}
                selectsEnd
                startDate={this.state.startDate}
                endDate={this.state.startDate}
                minDate={this.state.startDate}
                onChange={date => this.setState(state => ({
                  endDate: date
                }))}
              />
          </div>
          </div>
        </div>
        <div class="rowe">
          <div class="column">
          <p>Pretraži po imenu klijenta</p>
          <input type="text" name="name" style={{width: "200px"}} placeholder="Ime klijenta"/>
            
          </div>
          
          <div class="column">
          <p>Odaberite broj sobe</p>
            <input type="number" id="brSobe" name="brSobe"  min="1" max="13"  onInput={this.handleChangeBrSobe.bind(this)} style={{width: "130px"}} placeholder="Broj sobe [1,13]"></input>
          </div>

          <div class="column">
             
          </div>

        </div>
            
        </div>
        
        <div className="container mb-5 mt-5 text-left">
        <h4>Lista</h4>
        <hr></hr>
        
        <table class="table table-hover"  >
          <thead>
            <tr>
              <th>Naziv specijalne ponude</th>
              <th>Važi od</th>
              <th>Važi do</th>
              <th>Sobe u ponudi</th>
              <th>Veličina popusta [%]</th>
            </tr>
          </thead>
          <tbody>
          {this.state.rezervacija.map((result) => {
            return (
             
                 <tr>
                  <td>{result.nazivSpecPon}</td>
                  <td>{result.vaziOd}</td>
                  <td>{result.vaziDo}</td>
                  <td>{result.sobeNaPopustu}</td>
                  <td>{result.popust}</td>
                </tr>
             
            )
          })}
           
            
          </tbody>
        </table>
       
            
      </div>
     
      </div>
        
      </>
    );
  }
}

export default RadnikPregledSpecPon
