import React, {Component} from 'react';
import {DayPilot, DayPilotScheduler} from "daypilot-pro-react";

class Scheduler extends Component {

    constructor(props) {
        super(props);

        this.state = {
            startDate: "2021-03-01",
            days: 30,
            scale: "Day",
            timeHeaders: [
                { groupBy: "Month"},
                { groupBy: "Day", format: "d"}
            ],
            resources: [
                { name: "Soba 1", id: "R1"},
                { name: "Soba 2", id: "R2"},
                { name: "Soba 3", id: "R3"},
                { name: "Soba 4", id: "R3"},
                { name: "Soba 5", id: "R3"},
                { name: "Soba 6", id: "R3"},
                { name: "Soba 7", id: "R3"},
                { name: "Soba 8", id: "R3"},
                { name: "Soba 9", id: "R3"},
                { name: "Soba 10", id: "R3"},
                { name: "Soba 11", id: "R3"},
                { name: "Soba 12", id: "R3"},
            ]
        };
        this.promijeniMjesecNext = this.promijeniMjesecNext.bind(this);
    }
    promijeniMjesecNext(date) {
        
        date.setMonth(date.getMonth() + +1);
        
        this.setState(state => ({
          ...state,
          startDate: date
        }))
        
      }

    render() {
        var {...config} = this.state;
        return (
            <div>
                
                
                <DayPilotScheduler
                    {...config}
                />
                <button onClick={this.handleClick}>
                    Next
                </button>
            </div>
        );
    }
}

export default Scheduler;
