import React, { Component } from 'react'
import moment from 'moment'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Card from 'react-bootstrap/Card'
import Table from 'react-bootstrap/Table'
import DatePicker from "react-datepicker";

import NavbarRadnik from '../NavbarRadnik'
import './RadnikPregledRez.css'
import Datumcic from '../DatumPregledRez'
import axios from 'axios';
import NumericInput from 'react-numeric-input';


const today = moment().format('YYYY-MM-DD')
class RadnikPregledRez extends Component {
  constructor(props) {
    super(props);

    const rezervacija = [];
    

    for (let i = 0; i < 10; i++) {
        rezervacija.push({
            in: "2021-03-04",
            out: "2021-03-10",
            soba: "Superior",
            ime: "Emina Zahirovic",
            brGost: "2"

        });
    }

    this.state = { 
      pocDate: today,
      brSobe: 1,
      brGostiju: 1,
      rezervacija };

      this.handleChangeDate = this.handleChangeDate.bind(this);
      this.handleChangeBrSobe = this.handleChangeBrSobe.bind(this);
}

handleChangeDate(event) {
  this.setState({pocDate: event.target.value, brSobe: this.state.brSobe, brGostiju:this.state.brGostiju, rezervacija: this.state.rezervacija});
}
handleChangeBrSobe(event) {
  this.setState({pocDate: this.state.pocDate , brSobe: event.target.value, brGostiju:this.state.brGostiju, rezervacija: this.state.rezervacija});
}
    /*         
  state = {
    selectedDate: today,
    nights: '',
    noOfRooms: '',
    roomType: '',
    occupied: '',
    availableRooms: [],
    roomTypes: [],
    availableChecked: true,
    occupiedChecked: false
  }*/

  render () {
    return (
      <>
      <NavbarRadnik/>
      <div className="maincontainer">
      
       
        <h1 className="mr-5 ml-5 mt-5">Pregled rezervacija</h1>

        <div className="filter">
            <Datumcic  onChange={this.handleChangeDate}/>
            <p>Broj sobe [1,13]</p>
            <input type="number" id="tentacles" name="tentacles"  min="10" max="100"  onInput={this.handleChangeBrSobe.bind(this)}></input>
            
            <p>Broj gostiju [1,6]</p>
            <NumericInput min={1} max={6} value={1}/>

        </div>

        <div className="container mb-5 mt-5 text-left">
        
        <table class="table table-hover"  >
          <thead>
            <tr>
              <th>Ime</th>
              <th>Dolazak</th>
              <th>Odlazak</th>
              <th>Soba</th>
              <th>Broj gostiju</th>
            </tr>
          </thead>
          <tbody>
          {this.state.rezervacija.map((result) => {
            return (
             
                 <tr>
                  <td>{result.ime}</td>
                  <td>{result.in}</td>
                  <td>{result.out}</td>
                  <td>{result.soba}</td>
                  <td>{result.brGost}</td>
                </tr>
             
            )
          })}
           
            
          </tbody>
        </table>
       
            
      </div>
     
      </div>
        
      </>
    );
  }
}

export default RadnikPregledRez
