import React, { Component } from 'react'
import moment from 'moment'

import NavbarRadnik from '../NavbarRadnik'
import './RadnikPregledRez.css'

import DatePicker from "react-datepicker";

import Axios from "axios"
import {  Route, withRouter } from "react-router-dom";


const today = moment().format('YYYY-MM-DD')
class RadnikPregledSpecPon extends Component {
  constructor(props) {
    super(props);

    var specPon = [];
    Axios.get("http://localhost:3001/pregledSpecijalnihPonuda").then((result, fields)=>{
      specPon=result.data;
      console.log(specPon)
      specPon.map((ponuda)=>{
        ponuda["sobeNaPopustu"]=ponuda.idoviSoba.split(',');
      })
      this.setState(state => ({
        ...state,
        specPon:specPon
      }))
    })


    this.state = { 
      startDate: new Date(),
      endDate: new Date(),
      text1:"",
      brSobe: "", //Za pretraživanje broja sobe koja je spada u spec ponudu
      specPon: specPon };

     
      this.handleChangeBrSobe = this.handleChangeBrSobe.bind(this);
      console.log(this.state.specPon)
}
Filtriraj=()=>{
  Axios.get("http://localhost:3001/filtrirajSpecijalnePonude?text1="+this.state.brSobe+"&br_gostiju="+this.state.brGostiju+"&ime="+this.state.ime+"&prezime="+this.state.prezime+"&start_date="+moment(this.state.startDate).format('YYYY-MM-DD')+"&end_date="+moment(this.state.endDate).format('YYYY-MM-DD')+"&mijenjanEndDate="+this.state.mijanjanEndDate).then((result, fields)=>{
    //     console.log(result.data)
    this.setState(state => ({
     ...state,
     rezervacije:result.data,
     imena:[],
     prezimena:[],
     sobe:[]
   }))
   var rezerv=result.data;
  })
}

handleChangeBrSobe(event) {
  this.setState(state =>({...state, brSobe: event.target.value}));

  
}
handleChangeText1(event) {
  this.setState(state => ({...state, text1: event.target.value}));
}
   

  render () {
    return (
      <>
      <NavbarRadnik props={this.state.info}/>
      <div className="maincontainer">
      
       
        <h1 className="naslov">Pregled specijalnih ponuda</h1>

        <div className="filter">
        <h4 className="animation">Filteri</h4>
       
        <div className="filt-dio-1">
        <div className="rowe">
          <p>Specijalne ponude u vremenskom opsegu:</p>
            <div className="column"> 
            <p>Od:</p>
            <DatePicker 
                selected={this.state.startDate}
                selectsStart
                startDate={this.state.startDate}
                endDate={this.state.endDate} 
                onChange={date => this.setState(state => ({
                  startDate: date
                }))}
              />
          </div>
        
          <div className="column">
            <p>Do:</p>
            <DatePicker 
                selected={this.state.endDate}
                selectsEnd
                startDate={this.state.startDate}
                endDate={this.state.startDate}
                minDate={this.state.startDate}
                onChange={date => this.setState(state => ({
                  endDate: date
                }))}
              />
          </div>
          </div>
        </div>
        
        <div class="rowe-sp">
          <div class="column-sp">
          <p>Pretražite po nazivu specijalne ponude</p>
          <input type="text" name="name" style={{width: "200px"}} onInput={this.handleChangeText1.bind(this)}  placeholder="Naziv specijalne ponude"/>
            
          </div>
          
          <div class="column-sp">
          <p>Pretražite da li je soba uključena u neku ponudu</p>
            <input type="number" id="brSobe" name="brSobe"  min="1" max="13"  onInput={this.handleChangeBrSobe.bind(this)} style={{width: "130px"}} placeholder="Broj sobe [1,13]"></input>
          </div>


        </div>
           <div class="rowe">
        <div class="column"> 
        <button type="button" style={{marginTop: "20px", marginBottom: "20px"}} class="btn btn-info btn-lg btn-block" onClick={this.Filtriraj.bind(this)}>Filtriraj</button>
        </div>
        </div> 
        </div>
        
        <div className="container-table">
        <h4 className="animation">Lista</h4>
        
        <table class="table table-hover"  >
          <thead>
            <tr align="center">
              <th>Naziv specijalne ponude</th>
              <th>Važi od</th>
              <th>Važi do</th>
              <th>Sobe u ponudi</th>
              <th>Veličina popusta [%]</th>
            </tr>
          </thead>
          <tbody>
          {this.state.specPon.map((result) => {
            return (
             
                 <tr align="center">
                  <td>{result.text1}</td>
                  <td>{moment(result.startDatePonude).format('YYYY-MM-DD')}</td>
                  <td>{moment(result.endDatePonude).format('YYYY-MM-DD')}</td>
                  <td>{result.idoviSoba}  </td>
                  <td>{result.popust}</td>
                </tr>
             
            )
          })}
           
            
          </tbody>
        </table>
      
       
      </div>
      
      </div>
        
      </>
    );
  }
}

export default withRouter(RadnikPregledSpecPon)
