import React, {Component} from 'react';
import {DayPilot, DayPilotScheduler} from "daypilot-pro-react";

class Scheduler extends Component {

    constructor(props) {
        super(props);

        this.state = {
            startDate: new Date(),
            /*startDate: "2021-03-01",*/
            days: 37,
            scale: "Day",
            timeHeaders: [
                { groupBy: "Month"},
                { groupBy: "Day", format: "d"}
            ],
            resources: [
                { name: "Soba 1", id: "R1"},
                { name: "Soba 2", id: "R2"},
                { name: "Soba 3", id: "R3"},
                { name: "Soba 4", id: "R3"},
                { name: "Soba 5", id: "R3"},
                { name: "Soba 6", id: "R3"},
                { name: "Soba 7", id: "R3"},
                { name: "Soba 8", id: "R3"},
                { name: "Soba 9", id: "R3"},
                { name: "Soba 10", id: "R3"},
                { name: "Soba 11", id: "R3"},
                { name: "Soba 12", id: "R3"},
            ],
            events: [
                {
                  id: 1,
                  text: "Event 1",
                  start: "2021-11-02T00:00:00",
                  end: "2021-11-05T00:00:00",
                  resource: "A"
                },
                // ...
              ]
        };
        this.promijeniMjesecNext = this.promijeniMjesecNext.bind(this);
        this.promijeniMjesecBack = this.promijeniMjesecBack.bind(this);
    }
    promijeniMjesecNext() {
        var d = new Date(this.state.startDate)
        d.setMonth(d.getMonth() + 1);
        console.log(d)
        
        this.setState(state => ({
          ...state,
          startDate: d
        }))
        
      }

      promijeniMjesecBack() {
        var d = new Date(this.state.startDate)
        d.setMonth(d.getMonth() - 1);
        console.log(d)
        
        this.setState(state => ({
          ...state,
          startDate: d
        }))
        
      }

    render() {
        var {...config} = this.state;
        return (
            <div>
                
                
                <DayPilotScheduler
                    {...config}
                    
                    ref={component => { this.scheduler = component && component.control; }}
                    
                />
                <button onClick={this.promijeniMjesecNext}>
                    Next
                </button>
                <button onClick={this.promijeniMjesecBack}>
                    Back
                </button>

                <button onClick={e => this.scheduler.message('Welcome!')}>Welcome</button>
            </div>
        );
    }
}

export default Scheduler;
